{
  "qa_retry_limit": 5,
  "inject_phase_reminders": true,
  "guardrails": {
    "enabled": false,
    "max_tool_calls": 400,
    "max_duration_minutes": 240,
    "profiles": {
      "architect": {
        "max_duration_minutes": 0,
        "max_tool_calls": 0,
        "max_consecutive_errors": 10
      }
    }
  },
  "docs": {
    "enabled": true,
    "doc_patterns": [
      "README.md",
      "CONTRIBUTING.md",
      "docs/**/*.md",
      "docs/**/*.rst",
      "**/CHANGELOG.md"
    ]
  },
  "ui_review": {
    "enabled": true,
    "trigger_paths": [
      "**/pages/**",
      "**/components/**",
      "**/views/**",
      "**/screens/**",
      "**/ui/**",
      "**/layouts/**"
    ],
    "trigger_keywords": [
      "new page",
      "new screen",
      "new component",
      "redesign",
      "layout change",
      "form",
      "modal",
      "dialog",
      "dropdown",
      "sidebar",
      "navbar",
      "dashboard",
      "landing page",
      "signup",
      "login form",
      "settings page",
      "profile page"
    ]
  },
  "swarms": {
    "mega": {
      "name": "Mega",
      "agents": {
        "architect": { "model": "opencode/gpt-5-nano" },
        "coder": { "model": "minimax-coding-plan/MiniMax-M2.5" },
        "explorer": { "model": "zai-coding-plan/glm-5" },
        "explore": { "model": "zai-coding-plan/glm-5" },
        "sme": { "model": "kimi-for-coding/k2p5" },
        "critic": { "model": "zai-coding-plan/glm-5" },
        "reviewer": { "model": "kimi-for-coding/k2p5" },
        "test_engineer": { "model": "zai-coding-plan/glm-5" },
        "docs": { "model": "kimi-for-coding/k2p5" },
        "designer": { "model": "chutes/XiaomiMiMo/MiMo-V2-Flash" }
      }
    },
    "paid": {
      "name": "Paid",
      "agents": {
        "architect": { "model": "opencode/gpt-5-nano" },
        "coder": { "model": "chutes/Qwen/Qwen3-Coder-Next" },
        "explorer": { "model": "nano-gpt/minimax/minimax-m2.5" },
        "explore": { "model": "nano-gpt/minimax/minimax-m2.5" },
        "sme": { "model": "chutes/openai/gpt-oss-120b-TEE" },
        "critic": { "model": "chutes/XiaomiMiMo/MiMo-V2-Flash" },
        "reviewer": { "model": "chutes/moonshotai/Kimi-K2.5-TEE" },
        "test_engineer": { "model": "chutes/Qwen/Qwen3-Coder-480B-A35B-Instruct-FP8-TEE" },
        "docs": { "model": "zai-coding-plan/glm-4.7-flash" },
        "designer": { "model": "chutes/XiaomiMiMo/MiMo-V2-Flash" }
      }
    },
    "local": {
      "name": "Local",
      "agents": {
        "architect": { "model": "ollama/qwen2.5:32b" },
        "coder": { "model": "VSAN2/qwen/qwen3-coder-30b" },
        "explorer": { "model": "VSAN/openai/gpt-oss-20b" },
        "explore": { "model": "VSAN/openai/gpt-oss-20b" },
        "sme": { "model": "VSAN/openai/gpt-oss-20b" },
        "critic": { "model": "VSAN/openai/gpt-oss-20b" },
        "reviewer": { "model": "VSAN/openai/gpt-oss-20b" },
        "test_engineer": { "model": "VSAN2/qwen/qwen3-coder-30b" },
        "docs": { "model": "VSAN/openai/gpt-oss-20b" },
        "designer": { "model": "VSAN/openai/gpt-oss-20b" }
      }
    }
  },
  "max_iterations": 5,
  "automation": {
    "mode": "hybrid",
    "capabilities": {
      "plan_sync": true,
      "evidence_auto_summaries": true,
      "decision_drift_detection": true,
      "phase_preflight": true,
      "config_doctor_on_startup": true,
      "config_doctor_autofix": true
    }
  }
}
